INSERT INTO products (category_id, name, description, price, stock, image, category_id) VALUES (3, 'ENERGIZER L675ZA-8ZM RAYOVAC HEARING AID BATTERIES #675 PK/8', 15.99, 130, 'RAYOVAC_L675ZA-8ZM_BATTERY_675__87636.PNG', 'Hearing Aid Battery L675ZA-8ZM is a popular battery for behind-the-ear hearing aids. All manufacturers color code this battery blue for easy identification. However this should not be confused with the high power ZA675P cochlear implant batteries that are also colour coded blue.');
INSERT INTO products (category_id, name, description, price, stock, image, category_id) VALUES (3, 'Revamp Progloss Perfect Blow Dryer Volume & Shine Brush', 239.99, 440, 'dr-2000product1__11588__30040__83552.PNG', 'Powerful drying and styling combined into one simple hair tool. The Revamp Progloss Perfect Blow Dry Volume and Shine Air Styler dries, detangles, smooths and styles your hair in just one pass. The oval ceramic barrel of the brush works to smooth and style hair, whilst the rounded edges add volume at the root, to help you create a salon-quality blow-dry at home. To ensure frizz-free styling, this multifunctional styler features 4 ionic jet emitters and a ceramic barrel. Customize your styling experience with 3 Heat/Speed Settings, including a cool option to set your hairstyles in place. The lightweight design and 9 foot salon swivel cable allow for quick and easy styling, with additional flexibility. Infused with Progloss super smooth oils for enhanced shine.');
